  // const [formData, setFormData] = useState({
  //   username: "",
  //   skills: 0,
  //   cur_pos:"abcd"
  // })
  var userData = JSON.parse(sessionStorage.getItem("loggedUser"));
  const[username,setusername] = useState(userData['username'])
  const[cur_pos,setCurPos] = useState(userData['cur_pos'])
  let a= new Array(3);
  a= userData['skills']
